<?php if (!empty($data['images'])): ?>
<div class="pull-right">
	<div class="btn-group" role="group">		<button type="button" class="btn btn-default active" id="gallery-display-vignettes" data-toggle="tooltip" title="Vignettes">{fa-icon photo}</button>		<button type="button" class="btn btn-default" id="gallery-display-tableau" data-toggle="tooltip" title="Tableau">{fa-icon navicon}</button>	</div></div><div class="text-left">	<h4>{fa-icon photo} Liste <small><?php echo $count = count($data['images']); ?> <?php echo ($count > 1) ? 'images' : 'image'; ?></small></h4></div><div class="vignettes-content">	<div id="gallery-table">		{gallery_table}	</div>	<div id="gallery-vignettes" class="row">		<?php foreach ($data['images'] as $image): ?>		<div class="gallery-item">			<div class="thumbnail">				<a class="thumbnail-link" data-toggle="tooltip" title="Visualiser" data-image="{image <?php echo $image['file_id']; ?>}" data-title="<?php echo $image['title']; ?>" data-description="<?php echo $image['description']; ?>"><img class="img-responsive" src="{image <?php echo $image['thumbnail_file_id']; ?>}" alt="" /></a>				<div class="actions">					<a href="{base_url}admin/gallery/image/<?php echo $image['image_id']; ?>/<?php echo url_title($image['title']); ?>.html" class="btn btn-outline btn-info btn-xs" data-toggle="tooltip" title="Ã‰diter"><i class="fa fa-pencil"></i></a>					<?php echo button_delete('{base_url}admin/gallery/image/delete/'.$image['image_id'].'/'.url_title($image['title']).'.html'); ?>				</div>			</div>		</div>		<?php endforeach; ?>	</div></div><?php else: ?><div class="alert alert-info text-center no-margin">	Aucune image pour le moment</div><?php endif; ?>